'use strict';

var vue = require('vue');
require('@babel/runtime/helpers/objectSpread2');
require('@babel/runtime/helpers/defineProperty');
require('@segment/analytics-next');
var loglevel = require('../../base/loglevel.js');
require('@toruslabs/base-controllers');
require('@web3auth/auth');
var index = require('../../base/errors/index.js');
require('../../base/wallet/index.js');
require('../../base/connector/connectorStatus.js');
require('../../base/connector/constants.js');
require('jwt-decode');
require('../../base/plugin/errors.js');
require('../../base/plugin/IPlugin.js');
require('@toruslabs/constants');
require('@toruslabs/http-helpers');
var useWeb3AuthInner = require('./useWeb3AuthInner.js');

const useWeb3AuthDisconnect = () => {
  const {
    web3Auth
  } = useWeb3AuthInner.useWeb3AuthInner();
  const loading = vue.ref(false);
  const error = vue.ref(null);
  const disconnect = async options => {
    try {
      if (!web3Auth.value) throw index.WalletInitializationError.notReady();
      error.value = null;
      loading.value = true;
      await web3Auth.value.logout(options);
    } catch (err) {
      loglevel.log.error("Error disconnecting", err);
      error.value = err;
    } finally {
      loading.value = false;
    }
  };
  return {
    loading,
    error,
    disconnect
  };
};

exports.useWeb3AuthDisconnect = useWeb3AuthDisconnect;
